mapper.update:
UPDATE icredit_workspace
SET del_flag = 'Y'
WHERE ID = #{id}

mapper.select:
select 1 from icredit_workspace
AND name = #{name}
limit 1

select w.id, w.status, w.del_flag, w.descriptor, w.director, w.name, w.create_time, w.create_user,
w.update_time, w.update_user
from icredit_workspace w, icredit_workspace_user u


        AND w.name like concat ('%', #{request.name}, '%')


        AND w.create_user = #{request.createUser}


        AND w.update_time > #{request.updateStartTime}


        AND #{request.updateEndTime} > w.update_time


        AND u.user_id = #{request.userId}

        AND w.id = u.space_id

order by w.create_time
