mapper.update:
UPDATE icredit_workspace
SET del_flag = 'Y' , status = '1'
WHERE ID = #{id}

mapper.select:
select DISTINCT w.id, w.status, w.del_flag, w.descriptor, w.director, w.name, w.create_time, w.create_user,
w.update_time, w.update_user
from icredit_workspace w, icredit_workspace_user u


        AND w.id = #{request.spaceId}


        AND w.name like concat ('%', #{request.name}, '%')


        AND w.update_user = #{request.updateUser}


        AND w.update_time > #{request.updateStartTime}


        AND #{request.updateEndTime} > w.update_time


        AND u.user_id = #{request.userId}

        AND del_flag = 'N'
        AND w.id = u.space_id

order by w.status asc, w.update_time desc
